%%x=[0 0.15 0.31 0.5 0.6 0.75];%%y=[1.0 1.004 1.031 1.117 1.223 1.422];function taula = difdiv(x,y)x=[0 0.15 0.31 0.5 0.6 0.75];y=[1.0 1.004 1.031 1.117 1.223 1.422];n=length(x');a=zeros(n);a(1:n,1)=y';for j=1:n-1for i=1:n-ja(i,j+1)=(a(i+1,j)-a(i,j))/(x(i+j)-x(i));endendtaula=[x',a]'V=vander(x)cp=V\y'  %coeficients del polinomi interpoladorvalor = polyval(cp,3) %valor del polinomi en 3%% graficat=0.5:0.1:2;pt=polyval(cp,t);hold onplot(t,pt,'g',x,y,'*')end