%%PARABOLA = ax^2 + bx + cx=[1 1.125 1.250 1.375 1.5 1.625 1.750 1.875 2.0];y=[0 0.169925 0.321928 0.459432 0.584962 0.700440 0.807355 0.906891 1];A=vander(x,3);b=y';sol=A\br=norm(b-A*sol)Z=0.5:0.1:2.2;parabola=sol(1)*Z.^2 + sol(2)*Z + sol(3);plot(x,y,'*',Z,parabola,'m')%%sol =%%     -0.34033703896104 %%     2.01071245021645 %%     -1.6647548961039 disp('la solucion es sol(1)*x^2+sol(2)*x+sol(3)')