%%RECTA = AX+Bx=[0 0.15 0.31 0.5 0.6 0.75];y=[1.0 1.004 1.031 1.117 1.223 1.422];b=y';A=vander(x,2)sol=A\br=norm(b-A*sol)%% NO TE OLVIDES DE DECIR COMO SERIA LA RECTA SUSTITUYENDO%% LAS X EN DONDE CORRESPONDEZ=0:0.05:1;%%solr(1)=a0 y solr(2)=a1recta=sol(1)+sol(2)*Z;plot(x,y,'*',Z,recta,'m','LineWidth',2),title('recta')