%%crear matrices y resolver UX=BU=zeros(15,15);b=zeros(15,1);for i=1:15for j=1:15if i<=j U(i,j)=cos(i.*j);  end  end  b(i)=tan(i);  end U b %%triangular superioramp=[U;b']';n=length(b);disp ('************Solucio del sistema AX=b per substitucio enrera')X=zeros(n,1);X(n)=amp(n,n+1)/amp(n,n);for i=(n-1):-1:1X(i)=(amp(i,n+1)-amp(i,i+1:n)*X(i+1:n))/amp(i,i);endX