%% Resolucio per substitucio enrerafunction X=enrera(A,B)amp=[A;B']';   %%meter A por columnas!!!n=length(B);disp ('************Solucio del sistema AX=b per substitucio enrera')X=zeros(n,1);X(n)=amp(n,n+1)/amp(n,n);for i=(n-1):-1:1X(i)=(amp(i,n+1)-amp(i,i+1:n)*X(i+1:n))/amp(i,i);endreturn%% o bien%% Resolucio per substitucio enreraA=[3 0 0 0; -2 4 0 0; 1 -1 2 0; -1 2 3 5];B=[8;-3;11;15];amp=[A;B']';n=length(B);disp ('************Solucio del sistema AX=b per substitucio enrera')X=zeros(n,1);X(n)=amp(n,n+1)/amp(n,n);for i=(n-1):-1:1X(i)=(amp(i,n+1)-amp(i,i+1:n)*X(i+1:n))/amp(i,i);endX