X=[0.25 0.50 0.75 1.00 1.25 1.5 1.75];Y=[0.40 0.50 0.90 1.28 1.60 1.66 2.02];Tabla=[X;Y]'%ajuste por recta%%y=a0+a1*xdisp('recta')A=[ones(size(X)); X]'b=Y';solr=A\bZ=0:0.25:2;%%solr(1)=a0 y solr(2)=a1recta=solr(1)+solr(2)*Z;plot(X,Y,'*',Z,recta,'c','LineWidth',2),title('recta')e=Y-(solr(1)+solr(2)*X);rrecta=norm(e)%%ajuste por polinomio de grado 4%%y=a0 + a1*x + a2*x**2 + a3*x**3 +a4*x**4disp('polinomio')A=vander(X); %%mirar help vanderA(:,1:2)=[]b=Y';[Q,R]=qr(A);b1=Q'*b;coef_pol=R\b1ZZ=0:0.1:2;pol=polyval(coef_pol,ZZ);e=Y-polyval(coef_pol,X);rpol=norm(e)plot(X,Y,'*',Z,recta,'c',ZZ,pol,'r','Linewidth',2)%%para una potenciadisp('potencia')A=[ones(size(X)); log(X)]'b=log(Y)';[Q,R]=qr(A);b1=Q'*b;p=R\b1Z=0:0.25:2;corba = exp(p(1)).*Z.^p(2);e=Y-(exp(p(1)).*X.^p(2));rcorba=norm(e)plot(X,Y,'*',Z,recta,'c',Z,corba,'r','LineWidth',2),title('corba')