%%ejemplo regresion linealX=[(0:2:10)',ones(6,1)]y=[0.002; 0.150; 0.294; 0.434; 0.570; 0.704]%%ecuaciones normalesB=X'*X;b=X'*y;%%solucion residuo minimox=B\b, er=(y-X*x);u=-.5:0.1:10.5;v=polyval(x,u);subplot(3,1,1),plot(0:2:10,y,'b*',u,v,'r'),title('recta')subplot(3,1,2),plot(0:2:10,er,'r'),title('residus')%%ejemplo regresion polinomialX=[(0:2:10).^2',(0:2:10)',ones(6,1)]y=[0.002; 0.150; 0.294; 0.434; 0.570; 0.704]%%ecuaciones normalesB=X'*Xb=X'*y%%solucion residuo minimox=B\b, ep=(y-X*x)u=-.5:0.1:10.5;v=polyval(x,u);subplot(3,1,3),plot(0:2:10,y,'b*',u,v,'g'),title('paraÌ€bola')%%estudio residuoe1=norm(er)e2=norm(ep)subplot(3,1,2),plot(0:2:10,er,'r',0:2:10,ep,'g'),title('residus')