%%apartado a A=[1 0 1; 1 sin(pi/3) cos(pi/3); 1 sin(2*pi/3) cos(2*pi/3); 1 sin(pi) cos(pi); 1 sin(4*pi/3) cos(4*pi/3); 1 sin(5*pi/3) cos(5*pi/3)]b=[1 1.6 1.4 0.6 0.2 0.6]'%%apartado b %%ecuaciones normalesdisp('ecuaciones normales')B=A'*AC=A'*bdisp('solucion por eq normales')x=B\Cdisp('vector residuo eq normales')r=norm(b-A*x)%%apartado c%%factorizacion QRdisp('factorizaciones QR')[Q,R]=qr(A)b1=Q*bdisp('solucion con QR')x1=R\b1disp('vector residuo QR')r1=norm(b-A*x1)t=0:0.1:10;H1=x(1)+x(2)*sin((2*pi*t)/12)+x(3)*cos((2*pi*t)/12);H2=x1(1)+x1(2)*sin((2*pi*t)/12)+x1(3)*cos((2*pi*t)/12);th = [0 2 4 6 8 10];plot(t,H1,'m',t,H2,'g'),title('comparacion de metodos')hold onscatter(th,b) 