%%Solucion del error cuadratico minimo para el stma linealA=[1 1 1 0; 1 0 1 2; 1 1 0 1; 0 -1 2 0; -1 1 -1 1];b=[2;3;4;2;1];%%escribir stma lineal en forma Ax=bx=A\br=norm(b-A*x)%%ecuaciones normales en forma Bx=cB=A'*A;c=A'*b;%%da la solucion de las ecuaciones normalesx=B\c%%metodo de QR[Q,R]=qr(A);b1=Q*b;sol=R\b1r1=norm(b1-A*sol)%%calcular el vector residuo de la solucionb2=[0;-1;2;-1];y=A\b2;r2=norm(b2-A*y)