%%minimos cuadraticosdisp('datos')X=[8 10 12 16 20 40];Y=[0.88 1.22 1.64 2.72 3.96 11.96];TABLA=[X;Y]'%%ajuste por recta ax+bdisp('Recta')A=[ones(size(X));X]'b=Y';solr=A\bZ=7.5:2:41;recta=solr(1)+solr(2)*Z;plot(X,Y,'*',Z,recta,'c','LineWidth',2),title('recta')e=Y-(solr(1)+solr(2)*X);rrecta=norm(e)%%ajuste por polinomio de grado 4 ax4+bx3+cx2+dx+edisp('polinomio')A=vander(X); A(:,1:2)=[]b=Y';[Q,R]=qr(A)b1=Q'*b;coef_pol=R\b1ZZ=7:2:40;pol=polyval(coef_pol,ZZ);e=Y-polyval(coef_pol,X);rpol=norm(e)plot(X,Y,'*',Z,recta,'c',ZZ,pol,'r','Linewidth',2);%%ajuste por potenciadisp('potencia')A=[ones(size(X));log(X)]'b=log(Y)';[Q,R]=qr(A);b1=Q'*b;p=R\b1Z=7.5:2:41;curva=exp(p(1)).*Z.^p(2);e=Y-(exp(p(1)).*X.^p(2));rcurva=norm(e)plot(X,Y,'*',Z,recta,'c',Z,curva,'r','linewidth',2),title('curva')