%% aproximando con F1, apartado Ifor k=1:1:15h1=10^(-k);df1(k)=((atan(sqrt(5)/5))-atan((sqrt(5)-h1)/5)) / h1 ;enda=1/6*ones(1,15); b=df1;      errorabs1= abs(a-b)'%%aproximando con F2, apartado IIfor k=1:1:15h2=10^(-k);df2(k)=((atan((sqrt(5)+h2)/5))-atan((sqrt(5)-h2)/5)) / (2*h2) ;enda=1/6*ones(1,15); b=df2;      errorabs2= abs(a-b)'%% representacion de los dos erroresplot(1:15,log(errorabs1),'g',1:15,log(errorabs2),'m')