 radiosEspectralesg = zeros(1,25); for N=6:30  A = zeros(N,N);  B = zeros(1,N);  for i = 1:N          %for matrix B          if (i == 1 || i == N)              B(i) = 3;          elseif (i == 2 || i == N-1)              B(i) = 2;          else              B(i) = 1;          end      for j = 1:N          %for matrix A          if (abs(i-j) <= 2) && (i ~= j)              A(i,j) = -1;          elseif i == j              A(i,j) = 5;          elseif abs(i-j) > 2              A(i,j) = 0;          end                     end    end%%para gauss-SeidelU=triu(A,1); L=tril(A,-1);    D=diag(diag(A));Bgs = -inv(L+D)*U;cgs = inv(L+D)*B';roBgs=max(abs(eig(Bgs))); radiosEspectralesg(N-5) = roBgs;endplot(6:30,radiosEspectralesg,'g')