%% ejercicio 12 . metodos iterativos (ap.2.3)A=[1 2 -2; 1 1 1; 2 2 1];b=[1;1;1]U=triu(A,1)   %%diagonal superiorL=tril(A,-1)    %%diagonal inferiorD=diag(diag(A))    %%diagonalBj=-(inv(D))*(L+U)    cj=(inv(D))*broBj=max(abs(eig(Bj)))        %% Radi espectral, si (roBj > 1) -> divergent %% norm(Bj)                 %% norm(Bj) > roBj %%x=[0;0;0];if roBj>=1    disp('divergente')    else    disp('convergente')        breakend