%%ejercicio 12clcdisp('dades')X=[0.25 0.50 0.75 1.00 1.25 1.50 1.75];Y=[0.40 0.50 0.90 1.28 1.60 1.66 2.02];TAULA=[X;Y]'%%ajuste para la rectadisp('recta')A=[ones(size(X)); X]'b=Y';solr=A\bZ=0:0.25:2;recta = solr(1)+solr(2)*Z;plot(X,Y,'*',Z,recta,'cyan','LineWidth',2),title('recta')e=Y-(solr(1)+solr(2)*X);rrecta=norm(e)%%ajuste para polinomio de grado 4disp('polinomio')A=vander(X);A(:,1:2)=[]b=Y';[Q,R]=qr(A);b1=Q'*b;coef_pol=R\b1ZZ=0:0.1:2;pol=polyval(coef_pol,ZZ);e=Y-polyval(coef_pol,X); rpol=norm(e)plot(X,Y,'*',Z,recta,'cyan',ZZ,pol,'r','LineWidth',2)