disp('recta')A=[ones(size(X)); X]'b=Y';solr=A\bZ=0:0.25:2;recta = solr(1)+solr(2)*Z;plot(X,Y,'*',Z,recta,'cyan','LineWidth',2),title('recta')e=Y-(solr(1)+solr(2)*X);rrecta=norm(e)disp('recta')A=[ones(size(X)); X]'b=Y';solr=A\bZ=0:0.25:2;recta = solr(1)+solr(2)*Z;plot(X,Y,'*',Z,recta,'cyan','LineWidth',2),title('recta')e=Y-(solr(1)+solr(2)*X);rrecta=norm(e)disp('polinomi')A=vander(X); A(:,1:2)=[]b=Y';[Q,R]=qr(A);b1=Q'*b;coef_pol=R\b1ZZ=0:0.1:2;pol=polyval(coef_pol,ZZ);e = Y-polyval(coef_pol,X);rpol=norm(e)plot(X,Y,'*',Z,recta,'cyan',ZZ,pol,'r','LineWidth',2)disp('recta')A=[ones(size(X)); log(X)]'b=log(Y)';[Q,R]=qr(A);b1=Q'*b;p=R\b1Z=0:0.25:2;corba = exp(p(1)).*Z.^p(2);e=Y-(exp(p(1)).*X.^p(2));rcorba=norm(e)plot(X,Y,'*',Z,recta,'cyan',Z,corba,'r','LineWidth',2),title('corba')