%%ejercicio 23 A=[1 1 1 0; 1 0 1 2;1 1 0 1; 0 -1 2 0; -1 1 -1 1];b=[2;3;4;2;1];%%ecuaciones normales . APARTADO 2B=A'*A;c=A'*b;%%solucion del sistema lineal Ax=bdisp('solucion stma lineal Ax=b')x=B\c%%comparamos residuos.  APARTADO 4disp('comparacion de residuos')y=[0;-1;2;-1];e1=norm(b-A*x)e2=norm(b-A*y)%%Resolucion para QR. APARTADO 3disp('metodo QR')[Q,R]=qr(A);b1=Q*b;sol=R\b1