%%ejemplo para examen%%definimos A y b. APARTADO 1A=[1 1 1 0; 1 0 1 2; 1 1 0 1;0 -1 2 0; -1 1 -1 1]b=[2;3;4;2;1]%%ecuaciones normales . APARTADO 2B=A'*Ac=A'*b%%solucion de residuo minimo.  APARTADO 3x=B\c%%comparamos residuos.  APARTADO 4y=[0;-1;2;-1]e1=norm(b-A*x)e2=norm(b-A*y)%%Resolucion para QR. APARTADO 3[Q,R]=qr(A);b1=Q*b;sol=R\b1%%numero de condicionamientodisp('condicionamiento de A')cond(A) cond(eye) %%cond(eye)=1