%%FUNCIONES PREDEFINIDASA\b = solucion del stma Ax=b%% factorizacion PA=LU[L,U,P]=lu(A)  %L triang inf y U triang sup,  pasos % 1= Ly=Pb % 2= Ux=y %%factorizacion A=LL'L=chol(A)  %%factorizacion A=QR[Q,R]=qr(A), con A=R  %%Q ortogonal, R triang inf, pasos  % 1= B=Q'b  % 2= Rx=B  %La matriz R=Q'A mismo numero de condicion que A  %%vectores y valores propios[V,D]=eig(A) %% descomposicion en valores singulares[S,D,V]=svd(A) %%NUMERO DE CONDICION  cond(A,p) %%MEDIDA DEL MAL CONDICIONAMIENTO      cond(eye)=1     cond(matsingular)=âˆž       rcond(A,P) %%MEDIDA DEL BUEN CONDICIONAMIENTO      rcond(eye)=1      rcond(matsingular)=0            %%VECTOR RESIDUO  r=Ax-Ax*=b-Ax*, %%donde Ax* es la solucion calculada y Ax la solucion exacta.