A=[3 -1 1; 1 4 -1; 1 -1 -5];b=[1;3;-2];D=diag(diag(A));L=tril(A,-1);U=triu(A,1);BGS=-inv(D+L)*UCGS=inv(D+L)*bx=A\b   % Solucio dei sistema%Comencem el metode iteratiux=[0; 0; 0];   % Ã©s e mateix que x=zeros(size(b));r=A*x-b;for k=1:10;  x=BGS*x+CGS;  r=norm(A*x-b); %residu  [k,x']  if r < 0.5*10^(-8)      break  endenditeracionsGS=k %iteracionseig(BGS); %radi espectralradiespectralGS=max(abs(eig(BGS)))if radiespectralGS<1  disp('convergente')  else  disp('divergente')  end  