A=[3 1 -1; 1 5 -1; -1 1 -4];b=[2 1 -3];U=triu(A,1);L=tril(A,-1);D=diag(diag(A));Bj=-(inv(D))*(L+U)cj=(inv(D))*b'roBj=max(abs(eig(Bj)))        %% Radi espectral, si (roBj > 1) -> divergent %%                           % eig(Bj) calcula els valors propis de la matriu Bjif(roBj<1)  disp('convergente')  else  disp('divergente')  endx=[0; 0; 0];error=1;for i=1:10    x=Bj*x+cj;    error=norm(b'-A*x);    [i,x']endsol=A\b'%%hacer a mano %%>> errorabs1=abs(sol(1)-0.810344876543210) errorabs1 =    4.89570031181330e-08     %%7 cifras%%>> errorabs1=abs(sol(1)-0.810344876543210)errorabs1 =    4.89570031181330e-08      %%7 cifras%%>> errorabs2=abs(sol(2)-0.155172489711934)errorabs2 =    7.59188305554659e-08     %%8%%>> errorabs3=abs(sol(3)- 0.586206954732510)errorabs3 =    5.81807857447103e-08     %%8%%>> errorrel1=errorabs1/sol(1)errorrel1 =    6.04150251245046e-08     %%8%%>> errorrel2=errorabs2/sol(2)errorrel2 =    4.89254685801891e-07     %%6%%>> errorrel3=errorabs3/sol(3)errorrel3 =    9.92495756821528e-08     %%8